public class Infirm2Action extends ActionSupport {                                                                                                                                                                                       
    private static final long serialVersionUID = 1L;                                                                                                                                                                                     
                                                                                                                                                                                                                                         
    HttpServletRequest request = ServletActionContext.getRequest();                                                                                                                                                                      
    HttpServletResponse response = ServletActionContext.getResponse();                                                                                                                                                                   
                                                                                                                                                                                                                                         
    private static final Logger logger = MiscUtils.getLogger();                                                                                                                                                                          
    private static final InfirmBedProgramManager bpm = SpringUtils.getBean(InfirmBedProgramManager.class);                                                                                                                               
    private ProgramManager pm = SpringUtils.getBean(ProgramManager.class);                                                                                                                                                               
    private AdmissionManager mgr = SpringUtils.getBean(AdmissionManager.class);                                                                                                                                                          
                                                                                                                                                                                                                                         
    /**                                                                                                                                                                                                                                  
     * Updates the current program for a provider                                                                                                                                                                                        
     *                                                                                                                                                                                                                                   
     * @param programId the program ID to set as current                                                                                                                                                                                 
     * @param providerNo the provider number                                                                                                                                                                                             
     */                                                                                                                                                                                                                                  
    public static void updateCurrentProgram(String programId, String providerNo) {                                                                                                                                                       
        if (programId == null || providerNo == null) {                                                                                                                                                                                   
            logger.warn("Cannot update current program: programId or providerNo is null");                                                                                                                                               
            return;                                                                                                                                                                                                                      
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        try {                                                                                                                                                                                                                            
            int programIdInt = Integer.parseInt(programId);                                                                                                                                                                              
            logger.debug("Updating the current program to " + programId + " for provider " + providerNo);                                                                                                                                
            bpm.setDefaultProgramId(providerNo, programIdInt);                                                                                                                                                                           
        } catch (NumberFormatException e) {                                                                                                                                                                                              
            logger.error("Error parsing programId to set a user's default program: " + programId, e);                                                                                                                                    
        } catch (Exception e) {                                                                                                                                                                                                          
            logger.error("Error updating current program", e);                                                                                                                                                                           
        }                                                                                                                                                                                                                                
    }                                                                                                                                                                                                                                    
                                                                                                                                                                                                                                         
    /**                                                                                                                                                                                                                                  
     * Main execute method that handles different actions                                                                                                                                                                                
     */                                                                                                                                                                                                                                  
    public String execute() throws Exception {                                                                                                                                                                                           
        String action = request.getParameter("action");                                                                                                                                                                                  
        if (action == null) {                                                                                                                                                                                                            
            return showProgram();                                                                                                                                                                                                        
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        switch (action) {                                                                                                                                                                                                                
            case "getProgramList":                                                                                                                                                                                                       
                return getProgramList();                                                                                                                                                                                                 
            case "getSig":                                                                                                                                                                                                               
                return getSig();                                                                                                                                                                                                         
            case "toggleSig":                                                                                                                                                                                                            
                return toggleSig();                                                                                                                                                                                                      
            default:                                                                                                                                                                                                                     
                return showProgram();                                                                                                                                                                                                    
        }                                                                                                                                                                                                                                
    }                                                                                                                                                                                                                                    
                                                                                                                                                                                                                                         
    /**                                                                                                                                                                                                                                  
     * Shows the program information                                                                                                                                                                                                     
     */                                                                                                                                                                                                                                  
    public String showProgram() throws Exception {                                                                                                                                                                                       
        logger.debug("====> inside showProgram action.");                                                                                                                                                                                
                                                                                                                                                                                                                                         
        LoggedInInfo loggedInInfo = LoggedInInfo.getLoggedInInfoFromSession(request);                                                                                                                                                    
        HttpSession session = request.getSession();                                                                                                                                                                                      
        session.setAttribute("infirmaryView_initflag", "true");                                                                                                                                                                          
        String providerNo = (String) session.getAttribute("user");                                                                                                                                                                       
        String archiveView = (String) session.getAttribute("archiveView");                                                                                                                                                               
                                                                                                                                                                                                                                         
                                                                                                                                                                                                                                         
        Integer facilityId = null;                                                                                                                                                                                                       
                                                                                                                                                                                                                                         
        // facility filtering                                                                                                                                                                                                            
        if (OscarProperties.getInstance().getBooleanProperty("FILTER_ON_FACILITY", "true")) {                                                                                                                                            
            facilityId = loggedInInfo.getCurrentFacility().getId();                                                                                                                                                                      
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        List<LabelValueBean> programBeans = bpm.getProgramBeans(providerNo, facilityId);                                                                                                                                                 
                                                                                                                                                                                                                                         
        //this one seems to be for the caisi case management page, and caseload screens                                                                                                                                                  
        session.setAttribute("infirmaryView_programBeans", programBeans);                                                                                                                                                                
                                                                                                                                                                                                                                         
                                                                                                                                                                                                                                         
        //set default program                                                                                                                                                                                                            
        int defaultprogramId = bpm.getDefaultProgramId(providerNo);                                                                                                                                                                      
        boolean defaultInList = false;                                                                                                                                                                                                   
                                                                                                                                                                                                                                         
        for (LabelValueBean programBean : programBeans) {                                                                                                                                                                                
            int id = Integer.parseInt(programBean.getValue());                                                                                                                                                                           
            if (defaultprogramId == id) {                                                                                                                                                                                                
                defaultInList = true;                                                                                                                                                                                                    
            }                                                                                                                                                                                                                            
        }                                                                                                                                                                                                                                
        if (!defaultInList) {                                                                                                                                                                                                            
            defaultprogramId = 0;                                                                                                                                                                                                        
        }                                                                                                                                                                                                                                
        int OriprogramId = 0;                                                                                                                                                                                                            
        if (!programBeans.isEmpty()) {                                                                                                                                                                                                   
            OriprogramId = Integer.parseInt(programBeans.get(0).getValue());                                                                                                                                                             
        }                                                                                                                                                                                                                                
        int programId = 0;                                                                                                                                                                                                               
        if (defaultprogramId != 0 && OriprogramId != 0) {                                                                                                                                                                                
            programId = defaultprogramId;                                                                                                                                                                                                
        } else {                                                                                                                                                                                                                         
            if (OriprogramId == 0) {                                                                                                                                                                                                     
                programId = 0;                                                                                                                                                                                                           
            }                                                                                                                                                                                                                            
            if (defaultprogramId == 0 && OriprogramId != 0) {                                                                                                                                                                            
                programId = OriprogramId;                                                                                                                                                                                                
            }                                                                                                                                                                                                                            
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        //I guess this overrides everything we've done??                                                                                                                                                                                 
        String sessionProgramId = (String) session.getAttribute(SessionConstants.CURRENT_PROGRAM_ID);                                                                                                                                    
        if (sessionProgramId != null) {                                                                                                                                                                                                  
            try {                                                                                                                                                                                                                        
                programId = Integer.parseInt(sessionProgramId);                                                                                                                                                                          
            } catch (NumberFormatException e) {                                                                                                                                                                                          
                logger.error("Invalid program ID in session: " + sessionProgramId, e);                                                                                                                                                   
            }                                                                                                                                                                                                                            
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        session.setAttribute(SessionConstants.CURRENT_PROGRAM_ID, String.valueOf(programId));                                                                                                                                            
                                                                                                                                                                                                                                         
        updateCurrentProgram(String.valueOf(programId), loggedInInfo.getLoggedInProviderNo());                                                                                                                                           
                                                                                                                                                                                                                                         
        if (programId != 0) {                                                                                                                                                                                                            
            session.setAttribute("case_program_id", String.valueOf(programId));                                                                                                                                                          
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        if (programId != 0) {                                                                                                                                                                                                            
            session.setAttribute("program_client_statuses", pm.getProgramClientStatuses(programId));                                                                                                                                     
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        String[] programInfo = bpm.getProgramInformation(programId);                                                                                                                                                                     
        if (programInfo[0] != null) {                                                                                                                                                                                                    
            session.setAttribute("infirmaryView_programAddress", programInfo[0].replaceAll("\\n", "<br>"));                                                                                                                              
        } else {                                                                                                                                                                                                                         
            session.setAttribute("infirmaryView_programAddress", "");                                                                                                                                                                    
        }                                                                                                                                                                                                                                
        if (programInfo[1] != null) {                                                                                                                                                                                                    
            session.setAttribute("infirmaryView_programTel", programInfo[1]);                                                                                                                                                            
        } else {                                                                                                                                                                                                                         
            session.setAttribute("infirmaryView_programTel", "");                                                                                                                                                                        
        }                                                                                                                                                                                                                                
        if (programInfo[2] != null) {                                                                                                                                                                                                    
            session.setAttribute("infirmaryView_programFax", programInfo[2]);                                                                                                                                                            
        } else {                                                                                                                                                                                                                         
            session.setAttribute("infirmaryView_programFax", "");                                                                                                                                                                        
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        Date dt;                                                                                                                                                                                                                         
                                                                                                                                                                                                                                         
        if (session.getAttribute("infirmaryView_date") != null) {                                                                                                                                                                        
            dt = (Date) session.getAttribute("infirmaryView_date"); //new Date(year,month-1,day);                                                                                                                                        
        } else {                                                                                                                                                                                                                         
            dt = new Date();                                                                                                                                                                                                             
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        //lets not load the demographic beans when in appointment view..no point                                                                                                                                                         
        org.oscarehr.PMmodule.model.Program p = this.pm.getProgram(programId);                                                                                                                                                           
        if (p != null) {                                                                                                                                                                                                                 
            String exclusiveView = p.getExclusiveView();                                                                                                                                                                                 
                                                                                                                                                                                                                                         
            if (exclusiveView != null && exclusiveView.equals("appointment"))                                                                                                                                                            
                return null;                                                                                                                                                                                                             
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        //release memory                                                                                                                                                                                                                 
                                                                                                                                                                                                                                         
        List<LabelValueBean> demographicBeans = bpm.getDemographicByBedProgramIdBeans(programId, dt, archiveView);                                                                                                                       
        List<LabelValueBean> filteredDemographicBeans = new ArrayList<LabelValueBean>();                                                                                                                                                 
        String clientStatusId = request.getParameter("infirmaryView_clientStatusId");                                                                                                                                                    
        if (clientStatusId != null) {                                                                                                                                                                                                    
            if (clientStatusId.equals("0")) {                                                                                                                                                                                            
                filteredDemographicBeans = demographicBeans;                                                                                                                                                                             
            } else {                                                                                                                                                                                                                     
                for (Iterator<LabelValueBean> iter = demographicBeans.iterator(); iter.hasNext(); ) {                                                                                                                                    
                    LabelValueBean bean = iter.next();                                                                                                                                                                                   
                    String demographicNo = bean.getValue();                                                                                                                                                                              
                    Admission admission = null;                                                                                                                                                                                          
                    List<Admission> admissions = null;                                                                                                                                                                                   
                    try {                                                                                                                                                                                                                
                        if (archiveView != null && archiveView.equals("true")) {                                                                                                                                                         
                            admissions = mgr.getAdmissions_archiveView(String.valueOf(programId), Integer.parseInt(demographicNo));                                                                                                      
                            for (Iterator<Admission> i1 = admissions.iterator(); i1.hasNext(); ) {                                                                                                                                       
                                admission = i1.next();                                                                                                                                                                                   
                                Integer csi = admission.getClientStatusId();                                                                                                                                                             
                                if (csi == null)                                                                                                                                                                                         
                                    csi = 0;                                                                                                                                                                                             
                                if (clientStatusId != null && clientStatusId.equals(csi.toString())) {                                                                                                                                   
                                    filteredDemographicBeans.add(bean);                                                                                                                                                                  
                                    break;                                                                                                                                                                                               
                                }                                                                                                                                                                                                        
                            }                                                                                                                                                                                                            
                        } else {                                                                                                                                                                                                         
                            admission = mgr.getCurrentAdmission(String.valueOf(programId), Integer.parseInt(demographicNo));                                                                                                             
                            if (admission != null) {                                                                                                                                                                                     
                                Integer csi = admission.getClientStatusId();                                                                                                                                                             
                                if (csi == null)                                                                                                                                                                                         
                                    csi = 0;                                                                                                                                                                                             
                                if (clientStatusId != null && clientStatusId.equals(csi.toString())) {                                                                                                                                   
                                    filteredDemographicBeans.add(bean);                                                                                                                                                                  
                                }                                                                                                                                                                                                        
                            }                                                                                                                                                                                                            
                        }                                                                                                                                                                                                                
                    } catch (NumberFormatException e) {                                                                                                                                                                                  
                        logger.error("Error parsing demographic number: " + demographicNo, e);                                                                                                                                           
                    }                                                                                                                                                                                                                    
                }                                                                                                                                                                                                                        
                request.setAttribute("infirmaryView_clientStatusId", clientStatusId);                                                                                                                                                    
            }                                                                                                                                                                                                                            
        } else {                                                                                                                                                                                                                         
            filteredDemographicBeans = demographicBeans;                                                                                                                                                                                 
        }                                                                                                                                                                                                                                
        session.setAttribute("infirmaryView_demographicBeans", filteredDemographicBeans);                                                                                                                                                
        response.getOutputStream().print(" ");                                                                                                                                                                                           
                                                                                                                                                                                                                                         
        return null;                                                                                                                                                                                                                     
    }                                                                                                                                                                                                                                    
                                                                                                                                                                                                                                         
    /**                                                                                                                                                                                                                                  
     * Static method to load program information                                                                                                                                                                                         
     */                                                                                                                                                                                                                                  
    public static void loadProgram(HttpServletRequest request, HttpServletResponse response) {                                                                                                                                           
        try {                                                                                                                                                                                                                            
            logger.debug("====> inside loadProgram action.");                                                                                                                                                                            
                                                                                                                                                                                                                                         
            LoggedInInfo loggedInInfo = LoggedInInfo.getLoggedInInfoFromSession(request);                                                                                                                                                
            HttpSession session = request.getSession();                                                                                                                                                                                  
            session.setAttribute("infirmaryView_initflag", "true");                                                                                                                                                                      
            String providerNo = (String) session.getAttribute("user");                                                                                                                                                                   
            String archiveView = (String) session.getAttribute("archiveView");                                                                                                                                                           
                                                                                                                                                                                                                                         
                                                                                                                                                                                                                                         
            Integer facilityId = null;                                                                                                                                                                                                   
                                                                                                                                                                                                                                         
            // facility filtering                                                                                                                                                                                                        
            if (OscarProperties.getInstance().getBooleanProperty("FILTER_ON_FACILITY", "true")) {                                                                                                                                        
                facilityId = loggedInInfo.getCurrentFacility().getId();                                                                                                                                                                  
            }                                                                                                                                                                                                                            
            List<LabelValueBean> programBeans = bpm.getProgramBeans(providerNo, facilityId);                                                                                                                                             
                                                                                                                                                                                                                                         
            //this one seems to be for the caisi case management page, and caseload screens                                                                                                                                              
            session.setAttribute("infirmaryView_programBeans", programBeans);                                                                                                                                                            
                                                                                                                                                                                                                                         
                                                                                                                                                                                                                                         
            //set default program                                                                                                                                                                                                        
            int defaultprogramId = bpm.getDefaultProgramId(providerNo);                                                                                                                                                                  
            boolean defaultInList = false;                                                                                                                                                                                               
                                                                                                                                                                                                                                         
            for (LabelValueBean programBean : programBeans) {                                                                                                                                                                            
                int id = Integer.parseInt(programBean.getValue());                                                                                                                                                                       
                if (defaultprogramId == id) {                                                                                                                                                                                            
                    defaultInList = true;                                                                                                                                                                                                
                }                                                                                                                                                                                                                        
            }                                                                                                                                                                                                                            
            if (!defaultInList) {                                                                                                                                                                                                        
                defaultprogramId = 0;                                                                                                                                                                                                    
            }                                                                                                                                                                                                                            
            int OriprogramId = 0;                                                                                                                                                                                                        
            if (!programBeans.isEmpty()) {                                                                                                                                                                                               
                OriprogramId = Integer.parseInt(programBeans.get(0).getValue());                                                                                                                                                         
            }                                                                                                                                                                                                                            
            int programId = 0;                                                                                                                                                                                                           
            if (defaultprogramId != 0 && OriprogramId != 0) {                                                                                                                                                                            
                programId = defaultprogramId;                                                                                                                                                                                            
            } else {                                                                                                                                                                                                                     
                if (OriprogramId == 0) {                                                                                                                                                                                                 
                    programId = 0;                                                                                                                                                                                                       
                }                                                                                                                                                                                                                        
                if (defaultprogramId == 0 && OriprogramId != 0) {                                                                                                                                                                        
                    programId = OriprogramId;                                                                                                                                                                                            
                }                                                                                                                                                                                                                        
            }                                                                                                                                                                                                                            
                                                                                                                                                                                                                                         
            //I guess this overrides everything we've done??                                                                                                                                                                             
            String sessionProgramId = (String) session.getAttribute(SessionConstants.CURRENT_PROGRAM_ID);                                                                                                                                
            if (sessionProgramId != null) {                                                                                                                                                                                              
                try {                                                                                                                                                                                                                    
                    programId = Integer.parseInt(sessionProgramId);                                                                                                                                                                      
                } catch (NumberFormatException e) {                                                                                                                                                                                      
                    logger.error("Invalid program ID in session: " + sessionProgramId, e);                                                                                                                                               
                }                                                                                                                                                                                                                        
            }                                                                                                                                                                                                                            
                                                                                                                                                                                                                                         
            session.setAttribute(SessionConstants.CURRENT_PROGRAM_ID, String.valueOf(programId));                                                                                                                                        
                                                                                                                                                                                                                                         
            updateCurrentProgram(String.valueOf(programId), loggedInInfo.getLoggedInProviderNo());                                                                                                                                       
                                                                                                                                                                                                                                         
                                                                                                                                                                                                                                         
            ProgramManager pm = SpringUtils.getBean(ProgramManager.class);                                                                                                                                                               
            AdmissionManager mgr = SpringUtils.getBean(AdmissionManager.class);                                                                                                                                                          
                                                                                                                                                                                                                                         
            if (programId != 0) {                                                                                                                                                                                                        
                session.setAttribute("case_program_id", String.valueOf(programId));                                                                                                                                                      
            }                                                                                                                                                                                                                            
                                                                                                                                                                                                                                         
            if (programId != 0) {                                                                                                                                                                                                        
                session.setAttribute("program_client_statuses", pm.getProgramClientStatuses(programId));                                                                                                                                 
            }                                                                                                                                                                                                                            
                                                                                                                                                                                                                                         
            String[] programInfo = bpm.getProgramInformation(programId);                                                                                                                                                                 
            if (programInfo[0] != null) {                                                                                                                                                                                                
                session.setAttribute("infirmaryView_programAddress", programInfo[0].replaceAll("\\n", "<br>"));                                                                                                                          
            } else {                                                                                                                                                                                                                     
                session.setAttribute("infirmaryView_programAddress", "");                                                                                                                                                                
            }                                                                                                                                                                                                                            
            if (programInfo[1] != null) {                                                                                                                                                                                                
                session.setAttribute("infirmaryView_programTel", programInfo[1]);                                                                                                                                                        
            } else {                                                                                                                                                                                                                     
                session.setAttribute("infirmaryView_programTel", "");                                                                                                                                                                    
            }                                                                                                                                                                                                                            
            if (programInfo[2] != null) {                                                                                                                                                                                                
                session.setAttribute("infirmaryView_programFax", programInfo[2]);                                                                                                                                                        
            } else {                                                                                                                                                                                                                     
                session.setAttribute("infirmaryView_programFax", "");                                                                                                                                                                    
            }                                                                                                                                                                                                                            
                                                                                                                                                                                                                                         
            Date dt;                                                                                                                                                                                                                     
                                                                                                                                                                                                                                         
            if (session.getAttribute("infirmaryView_date") != null) {                                                                                                                                                                    
                dt = (Date) session.getAttribute("infirmaryView_date"); //new Date(year,month-1,day);                                                                                                                                    
            } else {                                                                                                                                                                                                                     
                dt = new Date();                                                                                                                                                                                                         
            }                                                                                                                                                                                                                            
                                                                                                                                                                                                                                         
            //lets not load the demographic beans when in appointment view..no point                                                                                                                                                     
            org.oscarehr.PMmodule.model.Program p = pm.getProgram(programId);                                                                                                                                                            
            if (p != null) {                                                                                                                                                                                                             
                String exclusiveView = p.getExclusiveView();                                                                                                                                                                             
                                                                                                                                                                                                                                         
                if (exclusiveView != null && exclusiveView.equals("appointment"))                                                                                                                                                        
                    return;                                                                                                                                                                                                              
            }                                                                                                                                                                                                                            
                                                                                                                                                                                                                                         
            //release memory                                                                                                                                                                                                             
                                                                                                                                                                                                                                         
            List<LabelValueBean> demographicBeans = bpm.getDemographicByBedProgramIdBeans(programId, dt, archiveView);                                                                                                                   
            List<LabelValueBean> filteredDemographicBeans = new ArrayList<LabelValueBean>();                                                                                                                                             
            String clientStatusId = request.getParameter("infirmaryView_clientStatusId");                                                                                                                                                
            if (clientStatusId != null) {                                                                                                                                                                                                
                if (clientStatusId.equals("0")) {                                                                                                                                                                                        
                    filteredDemographicBeans = demographicBeans;                                                                                                                                                                         
                } else {                                                                                                                                                                                                                 
                    for (Iterator<LabelValueBean> iter = demographicBeans.iterator(); iter.hasNext(); ) {                                                                                                                                
                        LabelValueBean bean = iter.next();                                                                                                                                                                               
                        String demographicNo = bean.getValue();                                                                                                                                                                          
                        Admission admission = null;                                                                                                                                                                                      
                        List<Admission> admissions = null;                                                                                                                                                                               
                        try {                                                                                                                                                                                                            
                            if (archiveView != null && archiveView.equals("true")) {                                                                                                                                                     
                                admissions = mgr.getAdmissions_archiveView(String.valueOf(programId), Integer.parseInt(demographicNo));                                                                                                  
                                for (Iterator<Admission> i1 = admissions.iterator(); i1.hasNext(); ) {                                                                                                                                   
                                    admission = i1.next();                                                                                                                                                                               
                                    Integer csi = admission.getClientStatusId();                                                                                                                                                         
                                    if (csi == null)                                                                                                                                                                                     
                                        csi = 0;                                                                                                                                                                                         
                                    if (clientStatusId != null && clientStatusId.equals(csi.toString())) {                                                                                                                               
                                        filteredDemographicBeans.add(bean);                                                                                                                                                              
                                        break;                                                                                                                                                                                           
                                    }                                                                                                                                                                                                    
                                }                                                                                                                                                                                                        
                            } else {                                                                                                                                                                                                     
                                admission = mgr.getCurrentAdmission(String.valueOf(programId), Integer.parseInt(demographicNo));                                                                                                         
                                if (admission != null) {                                                                                                                                                                                 
                                    Integer csi = admission.getClientStatusId();                                                                                                                                                         
                                    if (csi == null)                                                                                                                                                                                     
                                        csi = 0;                                                                                                                                                                                         
                                    if (clientStatusId != null && clientStatusId.equals(csi.toString())) {                                                                                                                               
                                        filteredDemographicBeans.add(bean);                                                                                                                                                              
                                    }                                                                                                                                                                                                    
                                }                                                                                                                                                                                                        
                            }                                                                                                                                                                                                            
                        } catch (NumberFormatException e) {                                                                                                                                                                              
                            logger.error("Error parsing demographic number: " + demographicNo, e);                                                                                                                                       
                        }                                                                                                                                                                                                                
                    }                                                                                                                                                                                                                    
                    request.setAttribute("infirmaryView_clientStatusId", clientStatusId);                                                                                                                                                
                }                                                                                                                                                                                                                        
            } else {                                                                                                                                                                                                                     
                filteredDemographicBeans = demographicBeans;                                                                                                                                                                             
            }                                                                                                                                                                                                                            
            session.setAttribute("infirmaryView_demographicBeans", filteredDemographicBeans);                                                                                                                                            
        } catch (Exception e) {                                                                                                                                                                                                          
            logger.error("Error in loadProgram", e);                                                                                                                                                                                     
        }                                                                                                                                                                                                                                
    }                                                                                                                                                                                                                                    
                                                                                                                                                                                                                                         
    /**                                                                                                                                                                                                                                  
     * Gets the signature status for a provider                                                                                                                                                                                          
     */                                                                                                                                                                                                                                  
    public String getSig() {                                                                                                                                                                                                             
        logger.debug("====> inside getSig action.");                                                                                                                                                                                     
        String providerNo = request.getParameter("providerNo");                                                                                                                                                                          
        if (providerNo == null) providerNo = (String) request.getSession().getAttribute("user");                                                                                                                                         
        Boolean onsig = bpm.getProviderSig(providerNo);                                                                                                                                                                                  
        request.getSession().setAttribute("signOnNote", onsig);                                                                                                                                                                          
        int pid = bpm.getDefaultProgramId(providerNo);                                                                                                                                                                                   
                                                                                                                                                                                                                                         
        request.getSession().setAttribute("programId_oscarView", "0");                                                                                                                                                                   
                                                                                                                                                                                                                                         
        String ppid = (String) request.getSession().getAttribute("case_program_id");                                                                                                                                                     
        if (ppid == null) {                                                                                                                                                                                                              
            request.getSession().setAttribute("case_program_id", String.valueOf(pid));                                                                                                                                                   
        } else {                                                                                                                                                                                                                         
            request.getSession().setAttribute("case_program_id", ppid);                                                                                                                                                                  
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        return null;                                                                                                                                                                                                                     
    }                                                                                                                                                                                                                                    
                                                                                                                                                                                                                                         
    /**                                                                                                                                                                                                                                  
     * Toggles the signature status for a provider                                                                                                                                                                                       
     */                                                                                                                                                                                                                                  
    public String toggleSig() {                                                                                                                                                                                                          
        logger.debug("====> inside toggleSig action.");                                                                                                                                                                                  
        String providerNo = request.getParameter("providerNo");                                                                                                                                                                          
        if (providerNo == null) providerNo = (String) request.getSession().getAttribute("user");                                                                                                                                         
        Boolean onsig = bpm.getProviderSig(providerNo);                                                                                                                                                                                  
        request.getSession().setAttribute("signOnNote", onsig);                                                                                                                                                                          
        return null;                                                                                                                                                                                                                     
    }                                                                                                                                                                                                                                    
                                                                                                                                                                                                                                         
    /**                                                                                                                                                                                                                                  
     * Gets the program list for a provider                                                                                                                                                                                              
     */                                                                                                                                                                                                                                  
    public String getProgramList() {                                                                                                                                                                                                     
        String providerNo = request.getParameter("providerNo");                                                                                                                                                                          
        String facilityIdStr = request.getParameter("facilityId");                                                                                                                                                                       
                                                                                                                                                                                                                                         
        PrintWriter out;                                                                                                                                                                                                                 
        try {                                                                                                                                                                                                                            
            out = response.getWriter();                                                                                                                                                                                                  
            out.print("<option value='all'>All Programs</option>");                                                                                                                                                                      
        } catch (IOException e) {                                                                                                                                                                                                        
            logger.error("Error getting response writer", e);                                                                                                                                                                            
            return null;                                                                                                                                                                                                                 
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        if (providerNo == null || facilityIdStr == null || providerNo.isEmpty() || facilityIdStr.isEmpty()) {                                                                                                                            
            return null;                                                                                                                                                                                                                 
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        int facilityId;                                                                                                                                                                                                                  
        try {                                                                                                                                                                                                                            
            facilityId = Integer.parseInt(facilityIdStr);                                                                                                                                                                                
        } catch (NumberFormatException e) {                                                                                                                                                                                              
            logger.error("Invalid facility ID: " + facilityIdStr, e);                                                                                                                                                                    
            return null;                                                                                                                                                                                                                 
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        try {                                                                                                                                                                                                                            
            List<LabelValueBean> programBeans;                                                                                                                                                                                           
            if (providerNo.equalsIgnoreCase("all")) {                                                                                                                                                                                    
                programBeans = bpm.getProgramBeansByFacilityId(facilityId);                                                                                                                                                              
            } else {                                                                                                                                                                                                                     
                programBeans = bpm.getProgramBeans(providerNo, facilityId);                                                                                                                                                              
            }                                                                                                                                                                                                                            
                                                                                                                                                                                                                                         
            for (LabelValueBean pb : programBeans) {                                                                                                                                                                                     
                out.print(String.format("<option value='%s'>%s</option>",                                                                                                                                                                
                        pb.getValue(),                                                                                                                                                                                                   
                        pb.getLabel()));                                                                                                                                                                                                 
            }                                                                                                                                                                                                                            
        } catch (Exception e) {                                                                                                                                                                                                          
            logger.error("Error retrieving program list", e);                                                                                                                                                                            
        }                                                                                                                                                                                                                                
                                                                                                                                                                                                                                         
        return null;                                                                                                                                                                                                                     
    }                                                                                                                                                                                                                                    
}